    function saveSVG($data, $name){

        $path = 'svgs/' . $name . '.svg';
        $general = 'svgs/General.svg';
        $svgString = '';
        $jsonData = [];

        if (file_exists($general)) {
            $svgString = file_get_contents($general);
        } else {}

        // $svgString .= "\r\n";
        // $svgString .= "<!-- " . $name . "-->";
        // $svgString .= "\r\n";
        $svgString .= $data;

        // Write in the file
        $fp = fopen($path, 'w');
        if (fwrite($fp, $data)) {
            fclose($fp);

            // Write in the file
            $fgen = fopen($general, 'w');
            if (fwrite($fgen, $svgString)) {
                fclose($fgen);
                return true;
            }else {
                return false;
            }
        }else {
            return false;
        }

    }
    

 
